import React from 'react';

const Login = ({history}) => {
  const handleLogin = () => {
         // Simulate login logic, for simplicity, just redirect to home
        history.push('/home');
       }
  return (
    <div class="main-wrapper login-body">
		<div class="login-wrapper">
			<div class="container">
				<div class="loginbox">
					<div class="login-left"> <img class="img-fluid" src="assets/img/logo.png" alt="Logo" /> </div>
					<div class="login-right">
						<div class="login-right-wrap">
							<h1>Login</h1>
							<p class="account-subtitle">Access to our dashboard</p>
							<form action="home.html">
								<div class="form-group">
									<input class="form-control" type="text" placeholder="Email"/> </div>
								<div class="form-group">
									<input class="form-control" type="text" placeholder="Password" /> </div>
								<div class="form-group">
									<button class="btn btn-primary btn-block" type="submit" onClick={handleLogin}>Login</button>
								</div>
							</form>
							<div class="login-or"> <span class="or-line"></span> <span class="span-or">or</span> </div>
							<div class="text-center forgotpass"><a href="forgot-password.html">Forgot Password?</a> </div>
						</div>
					</div>
				</div>
			</div>
		</div>
	</div>
  );
};

export default Login;














