import React, { useState, useEffect } from 'react';
import axios from 'axios';

function SignalHmu() {
  const [response, setResponse] = useState('');

  useEffect(() => {
    const sendValueToBackend = async () => {
      try {
        const response = await axios.post('/api/receive-value', { Value: 'Signal HMU' });
        setResponse(response.data.message);
      } catch (error) {
        console.error(error);
      }
    };

    // Automatically send the value to the backend when the component is mounted
    sendValueToBackend();
  }, []);

  return (
    <div>
      <p>{response}</p>
    </div>
  );
}

export default SignalHmu;
