import React from 'react';

const Login = ({history}) => {
  const handleLogin = () => {
    // Simulate login logic, for simplicity, just redirect to home  handleforgetpass
    history.push('/home');
  }
  const handleforgetpass = () => {
    history.push('/ForgetPassword');
  }
  return (
    <div className="main-wrapper login-body">
      <div className="login-wrapper">
        <div className="container">
          <div className="loginbox">
            <div className="login-left"> <img className="img-fluid" src="assets/img/logo.png" alt="Logo" /> </div>
            <div className="login-right">
              <div className="login-right-wrap">
                <h1>Login</h1>
                <p className="account-subtitle">Access to our dashboard</p>
                <form action="home.html">
                  <div className="form-group">
                    <input className="form-control" type="text" placeholder="Email"/> </div>
                  <div className="form-group">
                    <input className="form-control" type="text" placeholder="Password" /> </div>
                  <div className="form-group">
                    <button className="btn btn-primary btn-block" type="submit" onClick={handleLogin}>Login</button>
                  </div>
                </form>
                <div className="login-or"> <span className="or-line"></span> <span className="span-or">or</span> </div>
                <div className="text-center forgotpass" onClick={handleforgetpass}><a>Forgot Password?</a></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default Login;
