import React, { useState } from 'react';

const Sidebar = () => {
  const [isServiceMenuOpen, setIsServiceMenuOpen] = useState(false);
  const [isUsersMenuOpen, setIsUsersMenuOpen] = useState(false);

  const toggleServiceMenu = () => {
    setIsServiceMenuOpen(!isServiceMenuOpen);
    setIsUsersMenuOpen(false);
  };

  const toggleUsersMenu = () => {
    setIsUsersMenuOpen(!isUsersMenuOpen);
    setIsServiceMenuOpen(false);
  };

  return (
	<React.StrictMode>

    <div className="sidebar" id="sidebar">
      <div className="sidebar-inner slimscroll">
        <div id="sidebar-menu" className="sidebar-menu">
          <ul>
            <li>
              <a href="home.html">
                <i className="fas fa-tachometer-alt"></i> <span>Dashboard</span>
              </a>
            </li>
            <li className="list-divider"></li>
            <li className={`submenu ${isServiceMenuOpen ? 'active' : ''}`}>
              <a href="#" onClick={toggleServiceMenu}>
                <i className="fas fa-suitcase"></i> <span>Service List</span>{' '}
                <span className="menu-arrow"></span>
              </a>
              <ul className={`submenu_class ${isServiceMenuOpen ? 'show' : ''}`}>
                <li>
                  <a href="all-service-list.html">All Service</a>
                </li>
              </ul>
            </li>
            <li className={`submenu ${isUsersMenuOpen ? 'active' : ''}`}>
              <a href="#" onClick={toggleUsersMenu}>
                <i className="fas fa-user"></i> <span>Users List</span>{' '}
                <span className="menu-arrow"></span>
              </a>
              <ul className={`submenu_class ${isUsersMenuOpen ? 'show' : ''}`}>
                <li>
                  <a href="all-users.html">All Users</a>
                </li>
              </ul>
            </li>
          </ul>
        </div>
      </div>
    </div>
</React.StrictMode>

  );
};

export default Sidebar;
