import React from 'react';

const ForgetPassword = ({history}) => {
  const handleForgetPassword = () => {
    // Redirect to login page
    history.push('/login');
  };
  const handleLogin = () => {
    history.push('/login');
  }
  return (
    <div class="main-wrapper login-body">
      <div class="login-wrapper">
        <div class="container">
          <div class="loginbox">
            <div class="login-left"> <img class="img-fluid" src="assets/img/logo.png" alt="Logo"/> </div>
            <div class="login-right">
              <div class="login-right-wrap">
                <h1>Forgot Password?</h1>
                <p class="account-subtitle">Enter your email to get a password reset link</p>
                <form action="index.html">
                  <div class="form-group">
                    <input class="form-control" type="text" placeholder="Email"/> </div>
                  <div class="form-group mb-0">
                    <button class="btn btn-primary btn-block" type="submit" onClick={handleForgetPassword}>Reset Password</button>
                  </div>
                </form>
                <div class="text-center dont-have" onClick={handleLogin}>Remember your password? <a>Login</a></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ForgetPassword;
